using Microsoft.AspNetCore.Mvc;using MyStockApp.Services;using System;using System.Collections.Generic;using System.Threading.Tasks;using WebApplicationserver.Models;namespace MyStockApp.Controllers{    [ApiController]    [Route("api/[controller]")]    public class StocksController : ControllerBase    {        private readonly StockService _stockService;        //הזרקת הנתונים והפונקציות        public StocksController(StockService stockService)        {            _stockService = stockService;        }        [HttpGet("{symbol}/week")]        public async Task<ActionResult<List<StockData>>> GetLastWeek(string symbol)        {            var data = await _stockService.GetStockDataAsync(symbol);            return _stockService.FilterDataByPeriod(data, TimeSpan.FromDays(7));        }        [HttpGet("{symbol}/month")]        public async Task<ActionResult<List<StockData>>> GetLastMonth(string symbol)        {            var data = await _stockService.GetStockDataAsync(symbol);            return _stockService.FilterDataByPeriod(data, TimeSpan.FromDays(30));        }        [HttpGet("{symbol}/quarter")]        public async Task<ActionResult<List<StockData>>> GetLastQuarter(string symbol)        {            var data = await _stockService.GetStockDataAsync(symbol);            return _stockService.FilterDataByPeriod(data, TimeSpan.FromDays(90));        }        [HttpGet("{symbol}/year")]        public async Task<ActionResult<List<StockData>>> GetLastYear(string symbol)        {            var data = await _stockService.GetStockDataAsync(symbol);            return _stockService.FilterDataByPeriod(data, TimeSpan.FromDays(365));        }        [HttpGet("{symbol}/all")]        public async Task<ActionResult<List<StockData>>> GetAllData(string symbol)        {            var data = await _stockService.GetStockDataAsync(symbol);            return data;        }    }}